const App = () => {
    const searchMovies = async(title) =>{
        const response = await fetch(` ${API_URL}&s=${title}`);
        const data=await response.json();
        console.log(data.Search);

    }
    useEffect(()=>{
        searchMovies('Spiderman')
    },[]);

    return (
        <div className="app">
            <h1>MovieForrest</h1>
            <div className="search">
            <input
            placeholder='Search for movies'
            />
        </div>
        </div>


    );

}

export default App;
